mapper.insert:
  /*employee.insert*/ 
        INSERT INTO 
        	EMPLOYEE
	    (
	    	EMPLOYEE_ID
	        ,EMPLOYEE_NAME
	        ,SS_NUMBER
	        ,DEPARTMENT
	        ,EMPLOYEE_JOB
	        ,START_DATE
	        ,REGI_DATE
	        ,SAL
	        ,WORK_TYPE
	        ,EMPLOYEE_ADDRESS
        )
        VALUES
        (
	        (SELECT TO_NUMBER(NVL(MAX(EMPLOYEE_ID),1)) FROM EMPLOYEE)+1
	        ,#{employeeName}
	        ,#{ssNumber}
	        ,#{department}
	        ,#{employeeJob}
	        ,TO_DATE(#{startDate},'YY-MM-DD')
	        ,NULL
	        ,NULL
	        ,#{workType}
	        ,#{employeeAddress}
        )

mapper.select:
/* employee.employeeList */ SELECT A.EMPLOYEE_ID ,A.EMPLOYEE_NAME ,B.CODE_NAME AS DEPARTMENT ,C.CODE_NAME AS EMPLOYEE_JOB FROM ( SELECT EMPLOYEE_ID ,EMPLOYEE_NAME ,EMPLOYEE_JOB ,DEPARTMENT ,ROW_NUMBER() OVER(ORDER BY EMPLOYEE_ID DESC) AS NUMROW FROM EMPLOYEE ) A ,( SELECT CODE_ID ,CODE_NAME FROM EMPLOYEE_CODE WHERE CODE_TYPE='department'
) B ,( SELECT CODE_ID ,CODE_NAME FROM EMPLOYEE_CODE WHERE CODE_TYPE='job' ) C
WHERE A.DEPARTMENT=B.CODE_ID AND A.EMPLOYEE_JOB=C.CODE_ID AND A.NUMROW BETWEEN (#{pageNo}-1)*10+1 AND (#{pageNo})*10

/*employee.employeeSelectOne*/
SELECT A.EMPLOYEE_ID ,A.EMPLOYEE_NAME ,B.CODE_NAME AS DEPARTMENT ,C.CODE_NAME AS EMPLOYEE_JOB ,D.CODE_NAME AS WORK_TYPE ,TO_CHAR(A.START_DATE,'YYYY-MM-DD') AS START_DATE ,TO_CHAR(A.REGI_DATE,'YYYY-MM-DD') AS REGI_DATE ,A.SS_NUMBER ,TO_CHAR(NVL(A.REGI_DATE - A.START_DATE,CEIL(SYSDATE-A.START_DATE))) AS WORK_DAYS ,TO_CHAR(NVL(A.REGI_DATE,SYSDATE),'YYYY-MM-DD') AS END_DATE ,A.EMPLOYEE_ADDRESS
FROM EMPLOYEE A ,( SELECT CODE_ID ,CODE_NAME FROM EMPLOYEE_CODE WHERE CODE_TYPE='department' ) B ,( SELECT CODE_ID ,CODE_NAME FROM EMPLOYEE_CODE WHERE CODE_TYPE='job' ) C ,( SELECT CODE_ID ,CODE_NAME FROM EMPLOYEE_CODE WHERE CODE_TYPE='worktype' ) D
WHERE A.DEPARTMENT=B.CODE_ID AND A.EMPLOYEE_JOB=C.CODE_ID AND A.WORK_TYPE=D.CODE_ID AND A.EMPLOYEE_ID=#{employeeId}

mapper.update:
/* employee.employeeUpdate */
UPDATE
	EMPLOYEE
SET
	EMPLOYEE_NAME=#{employeeName}
	,DEPARTMENT=#{department}
	,SS_NUMBER=#{ssNumber}
	,EMPLOYEE_JOB=#{employeeJob}
	,START_DATE=#{startDate}
	,REGI_DATE=NVL(#{regiDate},NULL)
	,WORK_TYPE=#{workType}
WHERE
	EMPLOYEE_ID=#{employeeId}

mapper.delete:
/* employee.employeeDelete */
DELETE
FROM
	EMPLOYEE
WHERE
	EMPLOYEE_ID=#{employeeId}
